/**
 * Defines an event adapter for real-time event processing and integration.
 *
 * EventAdapter provides a hot-subscription pattern for processing live events as they occur, without replaying historical data. Unlike EventView which processes both historical and live events, adapters focus on real-time integration scenarios like external system synchronization, notifications, and live data feeds.
 *
 * Key Characteristics: - **Hot Subscription Only**: Processes new events, never replays history - **External Integration**: Designed for pushing data to external systems - **Transactional**: Each event is processed within a database transaction - **Error Handling**: Built-in error recovery and monitoring - **Consumer Groups**: Supports load balancing across multiple instances
 *
 * @example
 *
 * External system synchronization
 * ```typescript
 * const crmSyncAdapter: EventAdapter = {
 *   name: 'crm-synchronizer',
 *   consumerGroup: 'crm-sync',
 *   streamsToSubscribe: ['users', 'contacts'],
 *
 *   handleEvent: async (event) => {
 *     switch (event.type) {
 *       case 'UserCreated':
 *         await crmClient.createContact({
 *           externalId: event.domainId,
 *           email: event.data.email,
 *           name: event.data.name,
 *           source: 'app'
 *         });
 *         break;
 *
 *       case 'UserUpdated':
 *         await crmClient.updateContact(event.domainId, {
 *           email: event.data.email,
 *           name: event.data.name
 *         });
 *         break;
 *     }
 *   },
 *
 *   errorHandler: async (adapter, event, error) => {
 *     await errorTracking.report({
 *       adapter: adapter.name,
 *       eventType: event.type,
 *       eventId: event.id,
 *       error: error.message,
 *       timestamp: new Date()
 *     });
 *   }
 * };
 *
 * await registerAdapter(crmSyncAdapter);
 * ```
 *
 * @example
 *
 * Real-time notifications
 * ```typescript
 * const notificationAdapter: EventAdapter = {
 *   name: 'push-notifications',
 *   consumerGroup: 'notifications',
 *   streamsToSubscribe: ['orders', 'payments'],
 *
 *   handleEvent: async (event) => {
 *     const userId = event.data.userId || event.data.customerId;
 *     if (!userId) return;
 *
 *     let message: string;
 *     switch (event.type) {
 *       case 'OrderShipped':
 *         message = `Your order ${event.data.orderNumber} has been shipped!`;
 *         break;
 *       case 'PaymentFailed':
 *         message = 'Payment failed. Please update your payment method.';
 *         break;
 *       default:
 *         return;
 *     }
 *
 *     await pushNotificationService.send(userId, {
 *       title: 'Order Update',
 *       message,
 *       data: { eventId: event.id, type: event.type }
 *     });
 *   }
 * };
 * ```
 *
 * @see
 *
 * {@link registerAdapter} For adapter registration
 *
 * @see
 *
 * {@link EventView} For read-model projections with history replay
 *
 * @see
 *
 * {@link RawEventAdapter} For binary event processing
 */

