/**
 * Defines a long-running business process that coordinates across events and time.
 *
 * Sagas implement the Saga pattern for managing complex workflows that span multiple aggregates, external services, and time-based operations. They provide stateful event processing with support for timers, error handling, and process coordination.
 *
 * @template TimeoutNames - Union type of timer names this saga can schedule  @template InstanceData - Type of the saga's persistent state data
 *
 * Key Features: - **Stateful Processing**: Maintains state across multiple events - **Timer Support**: Schedule delays and recurring operations - **Error Handling**: Custom error handling and compensation logic - **Event Correlation**: Match events to specific saga instances - **Parallel Processing**: Configurable concurrency for high throughput
 *
 * @example
 *
 * Payment processing saga
 * ```typescript
 * interface PaymentData {
 *   orderId: string;
 *   amount: number;
 *   attempts: number;
 * }
 *
 * type PaymentTimers = 'timeout' | 'retry';
 *
 * export function paymentSaga() {
 *   return saga<PaymentTimers, PaymentData>('PaymentSaga')
 *     .subscribeStreams(['orders', 'payments'])
 *     .startOn('OrderCreated', {}, async (instance, event) => {
 *       instance.set('orderId', event.data.orderId);
 *       instance.set('amount', event.data.amount);
 *       instance.set('attempts', 0);
 *
 *       // Process payment
 *       await processPayment(event.data);
 *
 *       // Set timeout
 *       instance.upsertTimer('timeout', {
 *         isCron: false,
 *         timeout: 300000 // 5 minutes
 *       });
 *     })
 *     .on('PaymentSucceeded', {
 *       matchInstance: (event) => ({
 *         instanceProperty: 'orderId',
 *         value: event.data.orderId
 *       })
 *     }, async (instance, event) => {
 *       instance.removeTimer('timeout');
 *       instance.endSaga();
 *     })
 *     .onTimer('timeout', async (instance) => {
 *       const attempts = instance.get('attempts');
 *       if (attempts < 3) {
 *         instance.set('attempts', attempts + 1);
 *         await retryPayment(instance.get('orderId'));
 *       } else {
 *         await failPayment(instance.get('orderId'));
 *         instance.endSaga();
 *       }
 *     });
 * }
 * ```
 *
 * @see
 *
 * {@link SagaInstance} For instance state management
 *
 * @see
 *
 * {@link registerSaga} For saga registration
 *
 * @see
 *
 * {@link SagaScheduler} For advanced scheduling
 */

/**
