 *     .subscribeStreams(["payments"])
 *     .startOn("payment.created", {}, async (instance, event) => {
 *       instance.upsertTimer("ShortTimeout", {
 *         isCron: false,
 *         timeout: 5000,    // wait for 5s and then call.
 *       });
 *       instance.upsertTimer("PollEveryDay", {
